package assignment;

public class Addition {

	public static void main(java.lang.String[] args) {
		// TODO Auto-generated method stub

		int x = 12, y=22;
		System.out.println(x+y); 
		System.out.println(--x);    
		System.out.println(++y);   
		
		System.out.println(--x);   
		System.out.println(x--);
		System.out.println(x-- + y++);  



		 }

}
